import React from 'react';
import {Link} from 'react-router-dom';
import {useState} from 'react';
import dataUsers from '../DataUser/DataUser';
import users from '../DataUser/User';
import icon_men from './icon_men.png';
import check_list from './Checklist.jpg';
import './LoginPageCSS.css'


const LoginPage = () => {
  // для кнопок
  const [loginUser, setLogin] = useState('')
  const [passUser, setPass] = useState('')
  let [errorMessage] = useState(false)




  // проверка логина и пароля
  const loginUserChek = () =>{
    for (let key of dataUsers){
      
      if(key.log === loginUser && key.pass === passUser){
        console.log("Привет!")
        
        break
      } else {
        errorMessage = true
        console.log("Кажется что то не так :(")
      }
    }

  }

  return (
    
    <div className='login'>
      <div>
      <img 
          className='check_list'
          src={check_list} 
          alt='check_list' 
          height={800} width={1000}>
      </img>
      </div>

      <div className='login-page'>
        <img 
          className='icon_men'
          src={icon_men} 
          alt='icon-men' 
          height={150} width={150}>
        </img>

        <p className='login-sign-in'>
          <input 
            className='login-sign-inp'
            placeholder='login'
            type="text" 
            onChange={e => setLogin(e.target.value)}
            value = {loginUser}
          />

          <input
            className='login-sign-inp'
            placeholder='password'
            type="password" 
            onChange={e => setPass(e.target.value)}
            value = {passUser}
          />
        </p>

        <button 
          className='btn-login' 
          onClick={() => (console.log(loginUserChek()))}>
          Войти 
        </button>
        <button 
          className='btn-login'>
          <Link to='/'>ссылке</Link>
        </button>
        { errorMessage? (<p className='error'> Введены неправильные данные для вход!</p>):''}
        <p>
          Если нет аккаунта то зарегистрируйтесь по {" "}
          <Link to='/register'>ссылке</Link>
        </p>
      </div>
    </div>
  )
}

export default LoginPage
