import React from 'react';
import {Link} from 'react-router-dom';
import {useState} from 'react';
import dataUsers from '../DataUser/DataUser';
import icon_men from './icon_men.png';
import check_list from './Checklist.jpg';
import './LoginPageCSS.css'


const LoginPage = () => {
  // для кнопок
  const [loginUser, setLogin] = useState('')
  const [passUser, setPass] = useState('')
  
  // проверка логина и пароля
  const loginUserChek = () =>{
    for (let kay of dataUsers){
      
      if(kay.log === loginUser && kay.pass === passUser){
        console.log("Привет!")
        break
      } else {console.log("Кажется что то не так :(")}
      
    }
  }

  return (
    
    <div>
      <div className='login-page'>
      <img 
          src={icon_men} 
          alt='icon-men' 
          height={150} width={150}>
        </img>

        <p className='login-sign-in'>
          <input 
            className='login-sign-inp'
            placeholder='login'
            type="text" 
            onChange={e => setLogin(e.target.value)}
            value = {loginUser}
          />

          <input
            className='login-sign-inp'
            placeholder='password'
            type="password" 
            onChange={e => setPass(e.target.value)}
            value = {passUser}
          />
        </p>

        <button 
          className='btn-login' 
          onClick={() => (loginUserChek())}>
          Войти
        </button>

        <button className='btn-login'>
         <Link to='/register'>Регистрация</Link>
        </button>
      </div>
    </div>
  )
}

export default LoginPage
