import React from 'react'
import { Link } from 'react-router-dom';
import {useState} from 'react';
import dataUsers from '../DataUser/DataUser';
import reg_icon from './reg-icon.jpg';
import people from './people.png'
import '../LoginPage/LoginPageCSS.css'


const RegisterPage = () => {
  const [loginRegUser, setRegLogin] = useState('')
  const [passRegUser, setRegPass] = useState('')

  const RegUserChek = ()=> {
    const l = dataUsers.find(item => item.log === loginRegUser)
    console.log(l)
    if (loginRegUser !== '' && passRegUser !== '' && !dataUsers.find(item => item.log === loginRegUser)){
      dataUsers.push({log:loginRegUser,pass:passRegUser})
      console.log(dataUsers)
    }else (console.log('Нут !'))
  }

  return (
    <div className='login'>
      <div>
        <img 
            className='reg_icon'
            src={reg_icon} 
            alt='reg_icon' 
            height={800} width={1000}>
        </img>
      </div>

      <div className='login-page'>
        <img 
          className='people'
          src={people} 
          alt='people' 
          height={150} width={150}>
        </img>

        <p className='login-sign-in'>
          <input
            className='login-sign-inp'
            placeholder='login'
            type="text"
            onChange = {e => setRegLogin(e.target.value)}
            value = {loginRegUser}
          />
          <input
            className='login-sign-inp'
            placeholder='password'
            type="password"
            onChange = {e => setRegPass(e.target.value)}
            value = {passRegUser}
          />
        </p>
        <button
          className='btn-login'  
          onClick={() => (RegUserChek())}>Регистрация
        </button>

        <p>
          Если нет аккаунта то зарегистрируйтесь по ссылке 
          <a href='#'><Link to='/login'>Войти</Link></a>
        </p>

      </div>
    </div>
  )
}

export default RegisterPage
