import React from 'react'
import { Link } from 'react-router-dom';
import {useState} from 'react';
import dataUsers from '../DataUser/DataUser';
import {createUser} from '../DataUser/UserD'
import reg_icon from './reg-icon.jpg';
import people from './people.png'
import '../LoginPage/LoginPageCSS.css'

// export async function RegisterPage() {
//   const user = await createdUs()
//   return redirect
// }

const RegisterPage = () => {
  const [loginRegUser, setRegLogin] = useState('')
  const [passwordRegUser, setRegPassword] = useState('')
  

  let errorReg = document.getElementById('error')

  const RegUserChek = ()=> {
    if (loginRegUser !== '' && passwordRegUser !== '' && !dataUsers.find(item => item.log === loginRegUser)){
      createUser(loginRegUser, passwordRegUser)
      dataUsers.push({log:loginRegUser,password:passwordRegUser})
      console.log(dataUsers)
    } else {

      console.log('Нут !')
    }
  }

  return (
    <div className='login'>
      <div>
        <img 
            className='reg_icon'
            src={reg_icon} 
            alt='reg_icon' 
            height={800} width={1000}>
        </img>
      </div>

      <div className='login-page'>
        <img 
          className='people'
          src={people} 
          alt='people' 
          height={150} width={150}>
        </img>

        <p className='login-sign-in'>
          <input
            className='login-sign-inp'
            placeholder='login'
            type="text"
            onChange = {e => setRegLogin(e.target.value)}
            value = {loginRegUser}
          />
          <input
            className='login-sign-inp'
            placeholder='password'
            type="password"
            onChange = {e => setRegPassword(e.target.value)}
            value = {passwordRegUser}
          />
        </p>
        <button
          className='btn-login'  
          onClick={() => (RegUserChek())}>Регистрация
        </button>

        <p id='error' style={{display:'none'}} >Данный пользователь уже зарегистрирован!{" "}<Link to='/login'>ссылке</Link></p>

        <p>
          Есть вы вспомнили что у вас есть аккаунта то зайди по {" "}<Link to='/login'>ссылке</Link>
        </p>

      </div>
    </div>
  )
}

export default RegisterPage
