import React from 'react'
import { Link } from 'react-router-dom';
import {useState} from 'react';
import dataUsers from '../DataUser/DataUser';
import {createUser, getUsers} from '../DataUser/UserD'
import reg_icon from './reg-icon.jpg';
import people from './people.png'
import '../LoginPage/LoginPageCSS.css'



const RegisterPage = () => {
  const [loginRegUser, setRegLogin] = useState('')
  const [passwordRegUser, setRegPassword] = useState('')
  const [errorMessage, setErrorMessage] = useState(false)



  const RegUserChek = ()=> {
    if (loginRegUser !== '' && passwordRegUser !== ''){
      createUser({loginRegUser},{passwordRegUser})
      console.log(getUsers())
    } else {
      setErrorMessage (true)
      console.log('Нут !')
    }
  }

  return (
    <div className='login'>
      <div>
        <img 
            className='reg_icon'
            src={reg_icon} 
            alt='reg_icon' 
            height={800} width={1000}>
        </img>
      </div>

      <div className='login-page'>
        <img 
          className='people'
          src={people} 
          alt='people' 
          height={150} width={150}>
        </img>

        <p className='login-sign-in'>
          <input
            className='login-sign-inp'
            placeholder='login'
            type="text"
            onChange = {e => setRegLogin(e.target.value)}
            value = {loginRegUser}
          />
          <input
            className='login-sign-inp'
            placeholder='password'
            type="password"
            onChange = {e => setRegPassword(e.target.value)}
            value = {passwordRegUser}
          />
        </p>
        <button
          className='btn-login'  
          onClick={() => (RegUserChek())}>Регистрация
        </button>

        {errorMessage && (<p id='error'>Данный пользователь уже зарегистрирован!{" "}<Link to='/login'>ссылке</Link></p>)}

        <p>
          Есть вы вспомнили что у вас есть аккаунта то зайди по {" "}<Link to='/login'>ссылке</Link>
        </p>

      </div>
    </div>
  )
}

export default RegisterPage
