import React from 'react'
import { Link } from 'react-router-dom';
import {useState} from 'react';
import dataUsers from '../DataUser/DataUser';
import reg_icon from './reg-icon.jpg';
import people from './people.png'


const RegisterPage = () => {
  const [loginRegUser, setRegLogin] = useState('')
  const [passRegUser, setRegPass] = useState('')

  const RegUserChek = ()=> {
    const l = dataUsers.find(item => item.log === loginRegUser)
    console.log(l)
    if (loginRegUser !== '' && passRegUser !== '' && !dataUsers.find(item => item.log === loginRegUser)){
      dataUsers.push({log:loginRegUser,pass:passRegUser})
      console.log(dataUsers)
    }else (console.log('Нут !'))
  }

  return (
    <div className='login'>
      <div>
      <img 
          className='reg_icon'
          src={reg_icon} 
          alt='reg_icon' 
          height={800} width={1000}>
      </img>
      </div>

      <div>
      <img 
          className='reg_icon'
          src={reg_icon} 
          alt='reg_icon' 
          height={800} width={1000}>
      </img>

      </div>
    <p>
      <input 
        type="text"
        onChange = {e => setRegLogin(e.target.value)}
        value = {loginRegUser}
      />
      <input 
        type="password"
        onChange = {e => setRegPass(e.target.value)}
        value = {passRegUser}
      />
    </p>
    <button onClick={() => (RegUserChek())}>Регистрация</button>
    <button>
      <Link to='/login'>Войти</Link>
    </button>
</div>
  )
}

export default RegisterPage
