import React from 'react';
import {Link} from 'react-router-dom';
import {useState} from 'react';
import dataUsers from './DataUser';

const LoginPage = () => {
  // для кнопок
  const [loginUser, setLogin] = useState('')
  const [passUser, setPass] = useState('')
  
  // проверка логина и пароля
  const loginUserChek = () =>{
    for (let kay in dataUsers){
      if(dataUsers[kay].log === loginUser && dataUsers[kay].pass === passUser){
        console.log("Привет!")
      } else {console.log("Кажется уто то не так :(");console.log(dataUsers)}
      
    }
  }

  return (
    <div>
        <h1>Login</h1>
        <input 
          type="text" 
          onChange={e => setLogin(e.target.value)}
          value = {loginUser}
        />

        <input 
          type="text" 
          onChange={e => setPass(e.target.value)}
          value = {passUser}
        />

        <button onClick={() => (loginUserChek())}>Войти</button>

        <button>
         <Link to='/register'>Регистрация</Link>
        </button>
    </div>
  )
}

export default LoginPage
