import React from 'react'
import { Link } from 'react-router-dom';
import {useState} from 'react';
import dataUsers from './DataUser';


const RegisterPage = () => {
  const [loginRegUser, setRegLogin] = useState('')
  const [passRegUser, setRegPass] = useState('')

  const RegUserChek = ()=> {
    dataUsers.find(item => item.log === loginRegUser)
    if (loginRegUser !== '' && passRegUser !== ''){
      dataUsers.push({log:loginRegUser,pass:passRegUser})
      console.log(dataUsers)
    }else (console.log('Нут !'))
  }

  return (
    <div>
    <h1>Register</h1>
    <input 
      type="text"
      onChange = {e => setRegLogin(e.target.value)}
      value = {loginRegUser}
    />

    <input 
      type="text"
      onChange = {e => setRegPass(e.target.value)}
      value = {passRegUser}
    />

    <button onClick={() => (RegUserChek())}><Link to='/login'>Регистрация</Link></button>
    

    <button>
      <Link to='/login'>Войти</Link>
    </button>
</div>
  )
}

export default RegisterPage
