import React from 'react';
import {Link} from 'react-router-dom';
import {useState} from 'react';
import dataUsers from '../DataUser/DataUser';



const LoginPage = () => {
  // для кнопок
  const [loginUser, setLogin] = useState('')
  const [passUser, setPass] = useState('')
  
  // проверка логина и пароля
  const loginUserChek = () =>{
    for (let kay of dataUsers){
      
      if(kay.log === loginUser && kay.pass === passUser){
        console.log("Привет!")
        break
      } else {console.log("Кажется что то не так :(")}
      
    }
  }

  return (
    <div>
        <img src='./icon_men.png' alt='icon__men' height={100} width={100}></img>
        <p>
          <strong>Логин:</strong>
          <input 
            type="text" 
            onChange={e => setLogin(e.target.value)}
            value = {loginUser}
          />
          <strong>Пароль:</strong>
          <input 
            type="password" 
            onChange={e => setPass(e.target.value)}
            value = {passUser}
          />
        </p>
        <button onClick={() => (loginUserChek())}>Войти</button>
        <button>
         <Link to='/register'>Регистрация</Link>
        </button>
    </div>
  )
}

export default LoginPage
